mapper.select:
select 

from order
where order_id = #{orderId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

mapper.delete:
delete from order
where order_id = #{orderId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

mapper.update:
update order


    num = #{num,jdbcType=INTEGER},


where order_id = #{orderId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

update order
set num = #{num,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
